/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */

class Solution {
public:
    bool isPalindrome(ListNode* head) {
        
        if(head == nullptr || head->next == nullptr)
            return true;
    
        ListNode* middle = findMiddle(head);
        ListNode* half = flipList(middle->next);
        
        return isSame(head, half);
        
    }
    
    ListNode* findMiddle(ListNode* start){
        
        ListNode* slow = start;
        ListNode* fast = start;
        
        while(fast->next != nullptr && fast->next->next != nullptr){
            slow = slow->next;
            fast = fast->next->next;
        }
        
        return slow;
        
    }
    
    ListNode* flipList(ListNode* start){
        
        ListNode* store = nullptr;
        ListNode* update = nullptr;
        
        while(start != nullptr){
            store = start->next;
            start->next = update;
            update = start;
            start = store;
        }
        
        return update;
        
    }
    
    bool isSame(ListNode* head, ListNode* half){
        
        while(half != nullptr){
            if(head->val != half->val)
                return false;
            head = head->next;
            half = half->next;
        }
        
        return true;
        
    }
    
};
```

